sourcesContent:
import React, { useState, useEffect, useRef } from 'react';
import { Mail, Phone, MapPin, Briefcase, GraduationCap, Code, Award, Users, ExternalLink, BookOpen, Target, Lightbulb, Rocket, Trophy, Heart } from 'lucide-react';

const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('about');
  const [isVisible, setIsVisible] = useState(false);

  // Create refs for each section
  const sectionRefs = {
    about: useRef(null),
    experience: useRef(null),
    research: useRef(null),
    projects: useRef(null),
    internships: useRef(null),
    education: useRef(null),
    skills: useRef(null),
    certifications: useRef(null),
    extracurricular: useRef(null)
  };

  useEffect(() => {
    setIsVisible(true);

    // Create intersection observer
    const observerOptions = {
      root: null,
      rootMargin: '-20% 0px -80% 0px', // Trigger when section is 20% visible from top
      threshold: 0
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setActiveSection(entry.target.id);
        }
      });
    }, observerOptions);

    // Observe all sections
    Object.values(sectionRefs).forEach((ref) => {
      if (ref.current) {
        observer.observe(ref.current);
      }
    });

    return () => {
      observer.disconnect();
    };
  }, []);

  // Smooth scroll to section
  const scrollToSection = (sectionId) => {
    const element = sectionRefs[sectionId]?.current;
    if (element) {
      const headerHeight = 120; // Adjust based on your header height
      const elementPosition = element.offsetTop - headerHeight;

      window.scrollTo({
        top: elementPosition,
        behavior: 'smooth'
      });
    }
  };

  const resumeData = {
    name: "Dhanush Devadiga",
    title: "Software Engineer | AI/ML Specialist",
    contact: {
      email: "dhanushdevadiga1109@gmail.com",
      phone: "+91-7304867854",
      location: "Mumbai, Maharashtra, India",
      address: "B 801 Joy Homes, Opposite Ishwar Nagar, LBS Marg Bhandup (W), Mumbai 400078"
    },
    about: "Passionate Software Engineer specializing in Generative AI and Machine Learning with proven expertise in building scalable, context-aware conversational AI systems. Currently working at Navikenz, I've successfully deployed multiple GenAI solutions and created innovative AI-powered tools that significantly improve automation and user engagement.",
    experience: [
      {
        company: "Navikenz, Bangalore",
        role: "Software Engineer",
        period: "July 2024 - Present",
        achievements: [
          "Built scalable, context-aware conversational AI systems for multiple clients (2025)",
          "Created a comprehensive no-code agent platform that democratized AI application development (2025)",
          "Successfully deployed multiple GenAI solutions in production environment (2024)",
          "Developed innovative AI-powered tools that significantly improved automation and user engagement (2024)"
        ],
        responsibilities: [
          "Contributed to the development of Generative AI (GenAI) solutions, creating intelligent internal tools that enhanced automation and data-driven decision-making across the organization",
          "Designed and deployed a Retrieval-Augmented Generation (RAG)-based, agentic chatbot framework for multiple clients, leveraging NLP and machine learning to build scalable, context-aware conversational AI systems",
          "Built AI-powered photo processing tools that transform images using simple user prompts, eliminating the need for manual editing and enabling fully automated, high-quality visual outputs",
          "Collaborated with cross-functional teams to integrate AI capabilities into existing workflows, streamlining operations and significantly improving user engagement"
        ],
        projects: [
          {
            name: "GenBI",
            desc: "Developed an intelligent business intelligence platform that analyzed datasets and automatically generated charts and graphs based on user prompts or autonomous data understanding"
          },
          {
            name: "VFS POC",
            desc: "Developed a RAG-based chatbot for VFS Global to automate the e-visa on arrival process, reducing manual workflows by eliminating multiple steps"
          },
          {
            name: "Naviflow",
            desc: "Engineered a no-code agent platform that empowers users to build RAG-based applications through intuitive drag-and-drop component connections"
          },
          {
            name: "Photo Wizard",
            desc: "Built an AI-powered photo editing tool that automates background replacement, intelligent cropping, and standards compliance checks"
          }
        ]
      }
    ],
    research: {
      institution: "Jain Deemed to be University, Bangalore",
      period: "July - November 2022",
      role: "Researcher",
      project: "Self Defense and Safety Monitoring System",
      description: "The paper discusses an advanced smart safety device for rapid security, addressing limitations in existing solutions. Designed a user-friendly interface device with automatic responses, emphasizing quick activation in emergencies.",
      publication: "Published at 4th International Conference on Advances in Computing, Communication Control and Networking (ICAC3N), March 2023"
    },
    education: [
      {
        institution: "Jain University, Bangalore",
        degree: "Bachelor of Technology, Computer Science and Engineering",
        period: "2020 - 2024",
        grade: "CGPA 8.7/10"
      },
      {
        institution: "Mahesh PU College, Mangalore",
        degree: "Higher Secondary Examination, PUC (Karnataka State Board)",
        period: "2018",
        grade: "84.6%"
      },
      {
        institution: "Oxford High School, Bhandup",
        degree: "Secondary School Examination, SSC (Maharashtra State Board)",
        period: "2016",
        grade: "88.2%"
      }
    ],
    skills: {
      "Programming Languages": ["Python", "C", "C++", "SQL"],
      "Cloud/Databases": ["AWS", "MongoDB", "Postgres", "Azure"],
      "BI/Visualization": ["Power BI", "Tableau"],
      "Developer Tools": ["Git", "Docker", "Postman"],
      "Frameworks": ["Langchain", "Langgraph", "Agentic Frameworks", "FastAPI", "TensorFlow", "PyTorch", "Keras", "Flask"],
      "AI/ML": ["Deep Learning Fundamentals", "Natural Language Processing", "CV & IP", "Speech Recognition", "Reinforcement Learning"],
      "Microsoft Office": ["Word", "Excel", "PowerPoint", "Access"]
    },
    projects: [
      {
        name: "LegalLens AI",
        period: "May - July 2024",
        desc: "Implemented a RAG pipeline for document processing and intelligent retrieval using LangChain and LangGraph. Integrated Meta's LLaMA-2 (4B) model for accurate summarization and legal text generation."
      },
      {
        name: "Cuisine Quest",
        period: "March - April 2024",
        desc: "Developed a location-based restaurant recommendation web application using Flask, MySQL, and scikit-learn's BallTree algorithm for efficient geospatial queries."
      },
      {
        name: "Hyper-personalized Recommender System",
        period: "April - June 2023",
        desc: "Enhanced tailored user recommendations using Reinforcement Learning techniques. Evaluated and selected the most accurate recommender model for improved recommendation quality."
      },
      {
        name: "News Category Classifier Using NLP",
        period: "January - March 2023",
        desc: "Constructed a potent text summarization model for extracting vital information from extensive text datasets. Utilized LSTM and RNN techniques to build the model."
      }
    ],
    internships: [
      {
        company: "Navikenz, Bangalore",
        period: "January - July 2024",
        desc: "Engaged in diverse projects involving Python programming, data science methodologies, and database management. Gained hands-on experience in web development and cloud technologies."
      },
      {
        company: "Suvidha Foundation",
        period: "February - March 2023",
        desc: "Developed an advanced NLP text summarization model using state-of-the-art techniques to extract key information from extensive text data."
      },
      {
        company: "Intrainz Edutech",
        period: "November 2022 - January 2023",
        desc: "Implemented PCA on heart disease dataset during one-month industry training, reducing 13 features to 8 components with 96% accuracy."
      }
    ],
    certifications: [
      {
        name: "AI Practitioner - AWS",
        year: "2025",
        desc: "Demonstrated proficiency in AWS AI/ML services including SageMaker, Bedrock, and Comprehend."
      },
      {
        name: "Microsoft Azure AI Fundamentals",
        year: "2024",
        desc: "Gained foundational knowledge of Azure Cognitive Services for vision, speech, and language processing."
      },
      {
        name: "Robotics Workshop - Jain University",
        year: "2024",
        desc: "Learned fundamental robotics concepts and gained hands-on experience with IoT integration using Raspberry Pi."
      },
      {
        name: "Google Developer Students Club Workshop",
        year: "2023",
        desc: "Learned to implement and deploy language models using Google Cloud technologies."
      }
    ],
    achievements: [
      "Competed in intercollege hackathons hosted by Honeywell and Futurence Technologies (2023-2024)",
      "Specialized in AI/ML during BTech with focus on cutting-edge technologies (2022-2024)",
      "Published research paper on 'Self Defence and Safety Monitoring System' at International Conference (2022)"
    ],
    extracurricular: [
      {
        role: "Team Collaborator",
        organization: "Cross-functional AI development teams, Navikenz",
        period: "2024 - Present",
        desc: "Coordinated between development team and data science research teams for GenAI solution implementation."
      },
      {
        role: "Volunteer",
        organization: "Suvidha Foundation, Nagpur",
        period: "2023",
        desc: "Collected funds for educational development programs to support infrastructure improvements."
      }
    ]
  };

  const sections = [
    { id: 'about', label: 'About', icon: Users },
    { id: 'experience', label: 'Experience', icon: Briefcase },
    { id: 'research', label: 'Research', icon: BookOpen },
    { id: 'projects', label: 'Projects', icon: Code },
    { id: 'internships', label: 'Internships', icon: Lightbulb },
    { id: 'education', label: 'Education', icon: GraduationCap },
    { id: 'skills', label: 'Skills', icon: Award },
    { id: 'certifications', label: 'Certifications', icon: Trophy },
    { id: 'extracurricular', label: 'Activities', icon: Heart }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-950 via-blue-900 to-indigo-950">
      {/* Animated background */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute w-96 h-96 bg-blue-500 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-float top-0 left-0"></div>
        <div className="absolute w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-float-delayed bottom-0 right-0"></div>
        <div className="stars"></div>
      </div>

      {/* Fixed Header */}
      <div className={`fixed top-0 left-0 right-0 z-50 transition-all duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
        {/* Hero Section - Compact */}
        <div className="relative py-4 md:py-6 bg-gradient-to-r from-blue-950/95 via-blue-900/95 to-indigo-950/95 backdrop-blur-xl border-b border-blue-500/30">
          <div className="text-center z-10 px-4">
            <div className="mb-2 animate-scale-in">
              <div className="w-12 h-12 md:w-16 md:h-16 mx-auto bg-gradient-to-br from-blue-400 via-blue-500 to-indigo-600 rounded-full flex items-center justify-center text-lg md:text-2xl font-bold text-white shadow-2xl shadow-blue-500/50 border-2 border-blue-300/30">
                DD
              </div>
            </div>
            <h1 className="text-lg md:text-2xl font-bold text-white mb-1 tracking-tight">
              {resumeData.name}
            </h1>
            <p className="text-sm md:text-base text-blue-300 mb-2 font-light">
              {resumeData.title}
            </p>
            <div className="flex flex-wrap justify-center gap-2 md:gap-4 text-gray-200 text-xs">
              <a href={`mailto:${resumeData.contact.email}`} className="flex items-center gap-1 hover:text-blue-400 transition-all">
                <Mail size={12} />
                <span className="hidden sm:inline">{resumeData.contact.email}</span>
                <span className="sm:hidden">Email</span>
              </a>
              <a href={`tel:${resumeData.contact.phone}`} className="flex items-center gap-1 hover:text-blue-400 transition-all">
                <Phone size={12} />
                <span className="hidden sm:inline">{resumeData.contact.phone}</span>
                <span className="sm:hidden">Phone</span>
              </a>
              <div className="flex items-center gap-1 hover:text-blue-400 transition-all">
                <MapPin size={12} />
                <span>{resumeData.contact.location}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Navigation - Compact */}
        <div className="bg-blue-950/95 backdrop-blur-xl border-b border-blue-500/30 shadow-lg">
          <div className="container mx-auto px-2">
            <nav className="flex justify-center gap-1 py-2 overflow-x-auto scrollbar-hide">
              {sections.map(({ id, label, icon: Icon }) => (
                <button
                  key={id}
                  onClick={() => scrollToSection(id)}
                  className={`flex items-center gap-1 px-2 md:px-3 py-1.5 rounded-full transition-all whitespace-nowrap text-xs ${
                    activeSection === id
                      ? 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white shadow-lg'
                      : 'text-gray-300 hover:bg-blue-600/30 hover:text-white'
                  }`}
                >
                  <Icon size={14} />
                  <span className="hidden sm:inline">{label}</span>
                </button>
              ))}
            </nav>
          </div>
        </div>
      </div>

      {/* Content Sections - Scrollable */}
      <div className="pt-32 md:pt-36"> {/* Add padding to account for fixed header */}
        <div className="container mx-auto px-3 md:px-6 py-4 md:py-6 space-y-12 md:space-y-16">

          {/* About Section */}
          <section id="about" ref={sectionRefs.about} className="min-h-screen flex items-center">
            <div className="max-w-6xl mx-auto w-full">
              <h2 className="text-xl md:text-3xl font-bold text-white mb-3 md:mb-4 flex items-center gap-2">
                <Users className="text-blue-400" size={24} />
                About Me
              </h2>
              <div className="bg-white/10 backdrop-blur-xl rounded-xl md:rounded-2xl p-4 md:p-6 border border-blue-500/30">
                <p className="text-gray-100 text-xs md:text-sm leading-relaxed mb-4">
                  {resumeData.about}
                </p>
                <div className="grid md:grid-cols-2 gap-3 md:gap-4">
                  <div className="bg-gradient-to-br from-blue-600/30 to-indigo-600/30 rounded-xl p-3 md:p-4 border border-blue-400/40">
                    <h3 className="text-base md:text-lg font-semibold text-blue-300 mb-2 md:mb-3 flex items-center gap-2">
                      <Trophy size={18} />
                      Key Achievements
                    </h3>
                    <ul className="space-y-1.5">
                      {resumeData.achievements.map((achievement, i) => (
                        <li key={i} className="text-gray-200 text-xs flex items-start gap-2">
                          <Rocket size={12} className="text-blue-400 mt-0.5 flex-shrink-0" />
                          <span>{achievement}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                  <div className="bg-gradient-to-br from-indigo-600/30 to-blue-600/30 rounded-xl p-3 md:p-4 border border-indigo-400/40">
                    <h3 className="text-base md:text-lg font-semibold text-indigo-300 mb-2 md:mb-3 flex items-center gap-2">
                      <Target size={18} />
                      Professional Summary
                    </h3>
                    <ul className="space-y-1.5 text-gray-200 text-xs">
                      <li className="flex items-start gap-2">
                        <ExternalLink size={12} className="text-indigo-400 mt-0.5 flex-shrink-0" />
                        Current: Software Engineer at Navikenz
                      </li>
                      <li className="flex items-start gap-2">
                        <ExternalLink size={12} className="text-indigo-400 mt-0.5 flex-shrink-0" />
                        Expertise: GenAI, RAG Systems, NLP
                      </li>
                      <li className="flex items-start gap-2">
