sourcesContent:
import React, { useState, useEffect } from 'react';
import { Mail, Phone, MapPin, Briefcase, GraduationCap, Code, Award, Users, ChevronDown, ExternalLink, BookOpen, Target, Lightbulb, Rocket, Trophy, Heart } from 'lucide-react';

const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('about');
  const [isVisible, setIsVisible] = useState(false);
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    setIsVisible(true);
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const resumeData = {
    name: "Dhanush Devadiga",
    title: "Software Engineer | AI/ML Specialist",
    contact: {
      email: "dhanushdevadiga1109@gmail.com",
      phone: "+91-7304867854",
      location: "Mumbai, Maharashtra, India",
      address: "B 801 Joy Homes, Opposite Ishwar Nagar, LBS Marg Bhandup (W), Mumbai 400078"
    },
    about: "Passionate Software Engineer specializing in Generative AI and Machine Learning with proven expertise in building scalable, context-aware conversational AI systems. Currently working at Navikenz, I've successfully deployed multiple GenAI solutions and created innovative AI-powered tools that significantly improve automation and user engagement.",
    experience: [
      {
        company: "Navikenz, Bangalore",
        role: "Software Engineer",
        period: "July 2024 - Present",
        achievements: [
          "Built scalable, context-aware conversational AI systems for multiple clients (2025)",
          "Created a comprehensive no-code agent platform that democratized AI application development (2025)",
          "Successfully deployed multiple GenAI solutions in production environment (2024)",
          "Developed innovative AI-powered tools that significantly improved automation and user engagement (2024)"
        ],
        responsibilities: [
          "Contributed to the development of Generative AI (GenAI) solutions, creating intelligent internal tools that enhanced automation and data-driven decision-making across the organization",
          "Designed and deployed a Retrieval-Augmented Generation (RAG)-based, agentic chatbot framework for multiple clients, leveraging NLP and machine learning to build scalable, context-aware conversational AI systems",
          "Built AI-powered photo processing tools that transform images using simple user prompts, eliminating the need for manual editing and enabling fully automated, high-quality visual outputs",
          "Collaborated with cross-functional teams to integrate AI capabilities into existing workflows, streamlining operations and significantly improving user engagement",
          "Architected modular agent frameworks that allow rapid deployment of custom AI solutions across diverse business domains"
        ],
        projects: [
          {
            name: "GenBI",
            desc: "Developed an intelligent business intelligence platform that analyzed datasets and automatically generated charts and graphs based on user prompts or autonomous data understanding, enabling dynamic data visualization through natural language processing"
          },
          {
            name: "VFS POC",
            desc: "Developed a RAG-based chatbot for VFS Global to automate the e-visa on arrival process, reducing manual workflows by eliminating multiple steps including manual visa form filling and streamlining the entire application procedure"
          },
          {
            name: "Naviflow",
            desc: "Engineered a no-code agent platform that empowers users to build RAG-based applications through intuitive drag-and-drop component connections and agent prompt configuration, democratizing AI application development without requiring programming expertise"
          },
          {
            name: "Photo Wizard",
            desc: "Built an AI-powered photo editing tool that automates background replacement, intelligent cropping, and standards compliance checks (dimensions, aspect ratio, face visibility, background rules). Enabled prompt-driven edits where users specify requirements in natural language and the system performs end-to-end transformations without manual intervention, accelerating compliant photo generation for IDs, visas, and profiles"
          }
        ]
      }
    ],
    research: {
      institution: "Jain Deemed to be University, Bangalore",
      period: "July - November 2022",
      role: "Researcher",
      project: "Self Defense and Safety Monitoring System",
      description: "The paper discusses an advanced smart safety device for rapid security, addressing limitations in existing solutions. Designed a user-friendly interface device with automatic responses, emphasizing quick activation in emergencies. Integrated automatic response mechanisms to ensure quick activation and real-time reaction in critical situations. Implemented AI-powered emotion recognition system using machine learning algorithms to analyze voice patterns and pulse sensor data for automatic threat detection without manual activation",
      publication: "Published at 4th International Conference on Advances in Computing, Communication Control and Networking (ICAC3N), March 2023"
    },
    education: [
      {
        institution: "Jain University, Bangalore",
        degree: "Bachelor of Technology, Computer Science and Engineering",
        period: "2020 - 2024",
        grade: "CGPA 8.7/10"
      },
      {
        institution: "Mahesh PU College, Mangalore",
        degree: "Higher Secondary Examination, PUC (Karnataka State Board)",
        period: "2018",
        grade: "84.6%"
      },
      {
        institution: "Oxford High School, Bhandup",
        degree: "Secondary School Examination, SSC (Maharashtra State Board)",
        period: "2016",
        grade: "88.2%"
      }
    ],
    skills: {
      "Programming Languages": ["Python", "C", "C++", "SQL"],
      "Cloud/Databases": ["AWS", "MongoDB", "Postgres", "Azure"],
      "BI/Visualization": ["Power BI", "Tableau"],
      "Developer Tools": ["Git", "Docker", "Postman"],
      "Frameworks": ["Langchain", "Langgraph", "Agentic Frameworks", "FastAPI", "TensorFlow", "PyTorch", "Keras", "Flask"],
      "AI/ML": ["Deep Learning Fundamentals", "Natural Language Processing", "CV & IP", "Speech Recognition", "Reinforcement Learning"],
      "Microsoft Office": ["Word", "Excel", "PowerPoint", "Access"]
    },
    projects: [
      {
        name: "LegalLens AI",
        period: "May - July 2024",
        desc: "Objective was to implement a RAG pipeline for document processing and intelligent retrieval using LangChain and LangGraph. Integrated Meta's LLaMA-2 (4B) model for accurate summarization and legal text generation. Also, enabled users to upload legal files, ask questions, and create custom legal documents reducing manual effort and improving efficiency. Built a complete RAG pipeline with LangChain document processing, FAISS vector database for semantic search, and LangGraph workflow orchestration. Fine-tuned LLaMA-2 using LoRA for legal text generation and implemented FastAPI endpoints with OCR integration."
      },
      {
        name: "Cuisine Quest",
        period: "March - April 2024",
        desc: "Developed a location-based restaurant recommendation web application using Flask, MySQL, and scikit-learn's BallTree algorithm for efficient geospatial queries. Implemented machine learning-powered nearest neighbour search using Haversine distance metric to find restaurants within user-specified radius with O(log n) time complexity. Built secure user authentication system with MySQL database integration for managing user accounts and restaurant data with real-time processing capabilities. Engineered spatial indexing optimization using BallTree data structure with leaf_size tuning, enabling scalable geographic coordinate processing for large datasets. Created RESTful API endpoints for dynamic restaurant recommendations based on latitude/longitude coordinates and radius-based filtering. Applied advanced geospatial algorithms converting distance measurements from kilometers to degrees for accurate Earth surface calculations. Developed full-stack web application with responsive frontend templates and backend database connectivity for seamless user experience. Optimized query performance through NumPy array operations and efficient memory management for real-time restaurant discovery."
      },
      {
        name: "Hyper-personalized Recommender System using Reinforcement Learning",
        period: "April - June 2023",
        desc: "Aim was to enhance tailored user recommendations using Reinforcement Learning techniques. Evaluated and selected the most accurate recommender model, subsequently fine-tuning and optimizing it to improve recommendation quality and user accuracy. Implemented and optimized reinforcement learning algorithms to create a personalized recommendation engine, evaluating multiple models to achieve the highest accuracy for tailored user suggestions."
      },
      {
        name: "News Category Classifier Using NLP",
        period: "January - March 2023",
        desc: "Intended to construct a potent text summarization model for extracting vital information from extensive text datasets. Utilized LSTM and RNN techniques to build the model and developed a robust news categorization system that automatically categorizes news articles into distinct categories. Built bidirectional LSTM networks with attention mechanisms for text processing. Implemented comprehensive preprocessing pipeline with spaCy NER, TF-IDF vectorization, and Word2Vec embeddings for multi-class classification across 10+ news categories."
      },
      {
        name: "Predictive Healthcare Analytics for Cardiovascular Disease Detection",
        period: "November - December 2022",
        desc: "Developed a machine learning classification model to predict heart disease using clinical patient data with 303 records and 13 medical features including blood pressure, cholesterol, and ECG results. Implemented Random Forest Classifier with feature selection techniques (RFE and RFECV) to optimize model performance and reduce dimensionality. Applied Recursive Feature Elimination with Cross-Validation (RFECV) using 7-fold CV to automatically select optimal features, improving accuracy from 67.2% to 86.9%. Performed data preprocessing, train-test split (80-20), and model evaluation using scikit-learn library with Python pandas and NumPy. Achieved 86.9% classification accuracy on test data, demonstrating effective feature engineering and model optimization for healthcare applications. Utilized cross-validation techniques and RÂ² scoring metrics to ensure robust model selection and prevent overfitting. Built end-to-end ML pipeline from data loading to model serialization using pickle for deployment readiness."
      }
    ],
    internships: [
      {
        company: "Navikenz, Bangalore",
        period: "January - July 2024",
        desc: "Engaged in diverse projects involving Python programming, data science methodologies, and database management, applying practical skills to solve real-world problems. Gained hands-on experience in web development (HTML, CSS, JavaScript) and worked with cloud technologies on the Microsoft Azure platform, contributing to scalable and modern application solutions."
      },
      {
        company: "Suvidha Foundation",
        period: "February - March 2023",
        desc: "Developed an advanced NLP text summarization model using state-of-the-art techniques to extract key information from extensive text data. Improved text comprehension and information retrieval through concise and meaningful content summaries."
      },
      {
        company: "Intrainz Edutech",
        period: "November 2022 - January 2023",
        desc: "Implemented PCA on heart disease dataset during one-month industry training, reducing 13 features to 8 components with 96% accuracy. Applied PCA feature extraction techniques on 297 patient records, achieving 93% testing accuracy in cardiovascular risk prediction. Executed dimensionality reduction using PCA, optimizing model performance by 38% while preserving 95% data variance. Completed intensive one-month ML industry training covering Python, TensorFlow, and scikit-learn with hands-on project implementation."
      }
    ],
    certifications: [
      {
        name: "AI Practitioner - Amazon Web Services (AWS)",
        year: "2025",
        desc: "Demonstrated proficiency in AWS AI/ML services including SageMaker, Bedrock, and Comprehend. Applied machine learning workflows for model training, deployment, and monitoring on AWS infrastructure. Implemented generative AI solutions using AWS Bedrock foundation models. Designed scalable AI architectures leveraging Lambda, EC2, and containerized deployments. Validated expertise in responsible AI practices and governance frameworks."
      },
      {
        name: "Microsoft Azure AI Fundamentals",
        year: "2024",
        desc: "Gained foundational knowledge of Azure Cognitive Services for vision, speech, and language processing. Explored machine learning concepts using Azure Machine Learning Studio. Understood conversational AI development with Azure Bot Framework. Learned computer vision applications through Azure Computer Vision and Custom Vision services. Demonstrated knowledge of responsible AI principles and ethical considerations in AI development."
      },
      {
        name: "Robotics Workshop - Jain University Robotics Department",
        year: "2024",
        desc: "Learned fundamental robotics concepts and gained hands-on experience with IoT integration using Raspberry Pi microcontroller for embedded system development. Built a functional remote-controlled car from scratch using robotic chassis, DC motors, and basic electronic components with proper circuit design and wiring. Implemented wireless communication protocols by integrating both Bluetooth and WiFi modules for dual-mode remote control capabilities. Gained practical experience in Python programming on Raspberry Pi platform for GPIO pin control, motor driver functions, and wireless data communication. Developed understanding of IoT fundamentals including sensor integration, wireless protocols, and real-time command processing for robotic vehicle control systems."
      },
      {
